  conteoRubros() {
    let size = this.clientes.length;
    let rubrosSize = this.rubros.length;
    let cont = 0;
    for (let i = 0; i < rubrosSize; i++) {
      cont = 0;
      for (let j = 0; j < size; j++) {
        if (this.clientes[j].idCategory === this.rubros[i].id)
          cont = cont + 1;
      }
      this.quantitiesCLientesSelected.push(cont);
      cont = 0;
    }
  };
